package basics.loops.com;

/**
 * @author Phani kumar
 *
 */
public class Swithch {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int day=4;
		switch (day ) {
		case 4 :
			System.out.println("today is good day");
			break;
			case 2 :
				System.out.println("today is a good day");
				break;
				default :
					System.out.print("looking for good");
		}
	}

}
